from fastapi import FastAPI
from fastapi.middleware.cors import CORSMiddleware


def create_app():
    app = FastAPI(
        title="Python Sandpack Project",
        description="A beautiful Python project optimized for Sandpack IDE",
        version="1.0.0"
    )

    # Add CORS middleware
    app.add_middleware(
        CORSMiddleware,
        allow_origins=["*"],
        allow_credentials=True,
        allow_methods=["*"],
        allow_headers=["*"],
    )

    @app.get("/")
    async def root():
        return {"message": "Welcome to Python Sandpack Project!"}

    @app.get("/health")
    async def health_check():
        return {"status": "healthy", "environment": "sandpack"}

    return app
